INSERT INTO `oc_product_description` (`product_id`,`language_id`,`name`,`description`,`tag`,`meta_title`,`meta_description`,`meta_keyword`) VALUES (55,1,'Essential Phone','&lt;ul class=&quot;a-unordered-list a-vertical a-spacing-none&quot; style=&quot;margin-right: 0px; margin-left: 18px; color: rgb(148, 148, 148); padding: 0px; font-family: &amp;quot;Amazon Ember&amp;quot;, Arial, sans-serif; margin-bottom: 0px !important;&quot;&gt;&lt;li style=&quot;list-style: disc; word-wrap: break-word; margin: 0px;&quot;&gt;&lt;span class=&quot;a-list-item&quot; style=&quot;color: rgb(17, 17, 17);&quot;&gt;The Essential Phone is expertly crafted using titanium and ceramic, with an edge-to-edge Full Display and captures stunning images (even in low light) with the world\'s thinnest dual camera system ever built for a phone.&lt;/span&gt;&lt;/li&gt;&lt;li style=&quot;list-style: disc; word-wrap: break-word; margin: 0px;&quot;&gt;&lt;span class=&quot;a-list-item&quot; style=&quot;color: rgb(17, 17, 17);&quot;&gt;Beauty meets brawn - With a titanium body, a ceramic back and the attention to detail that went into building this device, you’ll feel the craftsmanship from the moment you pick it up.&lt;/span&gt;&lt;/li&gt;&lt;li style=&quot;list-style: disc; word-wrap: break-word; margin: 0px;&quot;&gt;&lt;span class=&quot;a-list-item&quot; style=&quot;color: rgb(17, 17, 17);&quot;&gt;Edge-to-edge Full Display - With the stunning edge-to-edge Full Display on the Essential Phone there\'s finally a large-screen device that fits comfortably in the palm of your hand.&lt;/span&gt;&lt;/li&gt;&lt;li style=&quot;list-style: disc; word-wrap: break-word; margin: 0px;&quot;&gt;&lt;span class=&quot;a-list-item&quot; style=&quot;color: rgb(17, 17, 17);&quot;&gt;The world’s thinnest dual camera system - The camera system consists of color and monochrome sensors to capture up to 200% more light than traditional phone cameras. This results in stunning images that are rich in color and detail, even in low light.&lt;/span&gt;&lt;/li&gt;&lt;li style=&quot;list-style: disc; word-wrap: break-word; margin: 0px;&quot;&gt;&lt;span class=&quot;a-list-item&quot; style=&quot;color: rgb(17, 17, 17);&quot;&gt;Accessories that simply click - With the phone\'s revolutionary magnetic connector, accessories can easily attach to your device so it\'s future-proof and always up-to-date.&lt;/span&gt;&lt;/li&gt;&lt;/ul&gt;','','Essential Phone','','');
